        {
            List<News> news = new List<News>
            {
                new News {Id = 1, Title = "Минобороны уличило коалицию США в географическом абсурде", AddDate = DateTime.Now, ShortDescription = "В министерстве обороны России назвали «географическим абсурдом» недавние заявления американского генерал-майора Джеймса Джеррарда, комментировавшего ситуацию на востоке Сирии.",
                    FullText = "В российском оборонном ведомстве прокомментировали недавние заявления командующего силами специальных операций (ССО) международной коалиции, возглавляемой США, генерал-майора Джеймса Джеррарда относительно ситуации на востоке Сирии после освобождения Дейр-эз-Зора. Однако, отметил Конашенков, Джеррард «переплюнул в своих фантазиях о происходящем на востоке Сирии всех сослуживцев».Представитель Минообороны напомнил американским коллегам, что город Меядин, который расположен в 40 километрах южнее Дейр-эз-Зора, уже более двух недель освобожден от боевиков ИГИЛ. «Поэтому тысячи сирийцев не “бегут” туда из Дейр-эз-Зора, а возвращаются в свои дома с гуманитарной помощью, которая доставляется российским Центром примирения враждующих сторон и международными гуманитарными организациями», — указал он." },
                new News {Id = 2, Title = "Заключенный создал финансовую пирамиду и позитил 6 млрд рублей", AddDate = DateTime.Now, ShortDescription = "Как стало известно «Ъ», осужденный за мошенничество и ожидающий вынесения еще одного приговора бывший банкир Максим Серкин организовал в колонии крупную финансовую пирамиду, деятельностью которой он руководил прямо из-под стражи. Подконтрольные доктору экономических наук, профессору Серкину кредитно-потребительские кооперативы действовали в 17 субъектах России четыре года, похитив за это время у вкладчиков более 6 млрд руб.",
                    FullText = "Такого масштабного мероприятия антикоррупционный главк не проводил уже давно. Всего за два дня, 30 и 31 октября, совместно с территориальными органами МВД сотрудники ГУЭБиПК провели обыски по 180 адресам в Москве, Санкт-Петербурге, Саратовской области, Краснодарском крае, Новосибирской области, Башкортостане, Татарстане, Приморском крае и еще девяти субъектах Российской Федерации. В ходе следственно-оперативных мероприятий были изъяты финансовая документация, печати более 200 фиктивных фирм и даже весьма редкий спецприбор для подделки подписей. В столице и Санкт-Петербурге, а также в Хабаровском и Приморском краях были задержаны восемь основных фигурантов дела. По ходатайству следственного департамента МВД, возбудившего уголовное дело по ст. 210 и ч. 2 ст. 172.2 УК РФ (организация преступного сообщества по привлечению денежных средств), все они были заключены судом под стражу. В ходе расследования было установлено, что действовавшие по всей России 22 кредитно-потребительских кооператива (КПК), имевшие лицензии Центробанка, начиная с 2013 года привлекали деньги вкладчиков под строительство объектов недвижимости — квартир и бизнес-центров. Клиентов заманивали выплатой повышенных дивидендов, достигавших 16−20% годовых, в то время как банки предлагали вкладчикам не более 10%. По словам сотрудников правоохранительных органов, пирамида, девизом которой являлось «Сохраним и преумножим», действовала абсолютно легально: все договоры с гражданами были оформлены надлежащим образом. При этом клиентов КПК даже возили по якобы строящимся объектам, а в офисах устраивали презентации проектов. В МВД отмечают, что среди вкладчиков было довольно много пенсионеров, каждый из которых отдал организаторам аферы от 30 тыс. до 400 тыс. руб. А несколько сотен человек из 25 тыс. лишившихся своих денег вложили в КПК от нескольких десятков до сотен миллионов рублей." },
                new News {Id = 3, Title = "ФСБ возбудила три уголовных дела из-за диверсий в Крыму", AddDate = DateTime.Now, ShortDescription = "Из-за повреждений газопроводов и ЛЭП в Крыму возбудили три уголовных дела, сообщили РИА Новости в пресс-службе регионального управления ФСБ.",
                    FullText = "На этой неделе в Крыму были повреждены два газопровода среднего давления — под Алуштой и недалеко от села Запрудное. Прорабатывается версия подрыва одного из них. Из-за этих инцидентов оказалось нарушено газоснабжение ряда населенных пунктов. Восстановительные работы на газопроводах завершили еще накануне, но подача газа потребителям в селах на Южном берегу Крыма начнется только сегодня. В «Крымэнерго» также сообщали о повреждении двух ЛЭП 110 кВ, которые питают электричеством Алушту и весь Южный берег Крыма." },
                new News {Id = 4, Title = "Франция начинает выдавать визы россиянам за 48 часов", AddDate = DateTime.Now, ShortDescription = "Российские туристы могут получать визы во Францию в течение 48 часов со 2 ноября. Эту информацию подтвердили ТАСС французские дипломаты.",
                    FullText = "«У нас все готово для выдачи туристических виз гражданам РФ в двухдневный срок начиная со 2 ноября, так как первого числа у нас выходной день», — пояснил собеседник агентства. Он отметил, что пока данное правило касается туристов, которые подают документы на визу в московском визовом центре. «Для других регионов это будет возможно, но позже», — заверил он." },
                new News {Id = 5, Title = "Спартак проиграл Севилье в Лиге чемпионов", AddDate = DateTime.Now, ShortDescription = "Московский «Спартак» проиграл испанской «Севилье» в четвертом туре группового этапа Лиги чемпионов. Об этом сообщает корреспондент «Ленты.ру».",
                    FullText = "Встреча прошла в Севилье и завершилась со счетом 2:1. На 30-й минуте хозяева вышли вперед после точного удара Клемана Лангле. Удвоил преимущество испанского клуба Эвер Банега, отличившийся на 59-й минуте. За 12 минут до конца основного времени матча форвард красно-белых Зе Луиш сократил разрыв в счете. Таким образом, в активе чемпионов России осталось пять очков. После четырех туров подопечные Массимо Карреры занимают третье место в группе." },
                new News {Id = 6, Title = "Путин заявил о неприятии россиянами давления извне на генетическом уровне", AddDate = DateTime.Now, ShortDescription = "Забота о своей стране и неприятие любого внешнего давления является основой российской государственности и генетическим кодом народа, заявил президент РФ Владимир Путин на торжественном приеме в Кремле по случаю Дня народного единства.",
                    FullText = "В своем поздравлении Путин обратился к истории этого праздника. «Четыре века отделяют нас от тех драматических событий, когда сам народ поднялся против смуты и безвластия, одолел внутренние распри и внешнюю угрозу, отстоял достоинство и независимость страны», — сказал он. Глава государства отметил, что «преданность отечеству объединяла тогда людей разных сословий, национальностей, вероисповеданий». С 2005 года 4 ноября в России отмечается День народного единства. Праздничная дата была установлена в память о событиях 1612 года, когда народное ополчение под предводительством Кузьмы Минина и Дмитрия Пожарского освободило Москву от польских интервентов. Исторически этот праздник связан с окончанием Смутного времени в России в XVII веке." },
                new News {Id = 7, Title = "РЖД возобновят продажу билетов в плацкарт и общие вагоны до 16 ноября", AddDate = DateTime.Now, ShortDescription = "«Российские железные дороги» возобновят продажу билетов на 2018 г. в плацкартные и общие вагоны не позднее 16 ноября, сообщает Федеральная антимонопольная служба.",
                    FullText = "Ранее сообщалось, что РЖД с 3 ноября приостановили продажу билетов в плацкартные и общие вагоны поездов дальнего следования, отправляющихся после Нового года. Это связано с тем, что ФАС еще не определилась с тарифами на 2018 г. «Минтранс России совместно с ФАС России выработают консолидированную позицию по вопросу установления тарифов на перевозки пассажиров в плацкартных и общих вагонах на 2018 г.», — говорится в сообщении ФАС. В службе напомнили, что перевозчик должен начать продажу билетов не позднее, чем за 45 дней до отправления. Исходя из этого, продажа на 1 января должна начаться до 16 ноября." }

            };
